mapper.insert:
INSERT INTO diary (
    user_id, title, content, category, tags, mood, weather,
    related_goal_id, image_urls, is_public, view_count, diary_date,
    create_time, update_time
) VALUES (
    #{userId}, #{title}, #{content}, #{category}, #{tags}, #{mood}, #{weather},
    #{relatedGoalId}, #{imageUrls}, #{isPublic}, #{viewCount}, #{diaryDate},
    #{createTime}, #{updateTime}
)

mapper.select:
SELECT * FROM diary
WHERE user_id = #{userId}
ORDER BY diary_date DESC, create_time DESC

SELECT * FROM diary
WHERE user_id = #{userId}
AND diary_date BETWEEN #{startDate} AND #{endDate}
ORDER BY diary_date DESC

SELECT * FROM diary
WHERE user_id = #{userId} AND category = #{category}
ORDER BY diary_date DESC

SELECT * FROM diary
WHERE user_id = #{userId}
ORDER BY create_time DESC
LIMIT #{limit}
