mapper.insert:
INSERT INTO goal (
    user_id, title, description, category_id, priority,
    deadline, progress, status, image_url, create_time, update_time
) VALUES (
    #{userId}, #{title}, #{description}, #{categoryId}, #{priority},
    #{deadline}, #{progress}, #{status}, #{imageUrl}, #{createTime}, #{updateTime}
)

mapper.delete:
DELETE FROM goal
WHERE id IN

    #{id}

mapper.select:
SELECT id, user_id, title, description, category_id, priority,
       deadline, progress, status, image_url, create_time, update_time
FROM goal
WHERE user_id = #{userId}
ORDER BY update_time DESC
LIMIT #{limit}
