mapper.update:
UPDATE reminder
SET is_read = #{isRead},
    update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) FROM reminder 
WHERE user_id = #{userId} 
  AND reminder_type = #{reminderType} 
  AND related_entity_id = #{relatedEntityId}

mapper.delete:
DELETE FROM reminder 
WHERE user_id = #{userId} 
  AND reminder_type = #{reminderType} 
  AND related_entity_id = #{relatedEntityId}
